from PISM.cpp import *
from PISM.options import *
import cpp
import util
import ssa

def verbPrintf(verbosity,com,msg,*args):
  msg = msg % args
  cpp._verbPrintf(verbosity,com,msg)

class VerbPrintf:
  def __init__(self,com,verbosity=1):
    self.com = com
    self.verbosity=verbosity

  def setverbosity(self,verbosity):
    self.verbosity=verbosity

  def println(self,msg,*args):
    msg = msg % args
    verbPrintf(self.verbosity,self.com,"%s\n",msg)

  def printlnv(self,verbosity,msg,*args):
    msg = msg % args    
    verbPrintf(verbosity,self.com,"%s\n",msg)
